import React, { useState } from 'react';
import './App.css';
import Form0 from './components/Form0'
import Step1 from './components/Step1'

import 'react-chat-elements/dist/main.css';
import { Avatar } from 'react-chat-elements';

import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";

function App() {
  const [form, setFormVisible] = useState(true);
  const [step, setStep1Visible] = useState(false);

  function renderSwitch(param) {
    setFormVisible(false);
    switch(param) {
      case 1: setStep1Visible(true);
      default: setFormVisible(true);
    }
  }

  return (
  <Router>
    <div>
      <Switch>
        <Route path="/" component={Form0}/>
        <Route path="/step" component={Step1}/>
      </Switch>
      <div style={{position:"absolute",right:"20px",bottom:"20px"}}>
        <Avatar
        src={'https://www.brandeps.com/icon-download/C/Chat-icon-vector-03.svg'}
        alt={'logo'}
        size="xlarge"
        type="circle flexible"/>
      </div>
      </div>
  </Router>
  );
}

export default App;
