import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Button,Form} from 'react-bootstrap';
import {Container,Row,Col} from 'react-bootstrap';

import { validate } from 'validate.js';
import constraints from './constraints';

class Form0 extends React.Component {
    constructor(props) {
      super(props);
      this.email = React.createRef();
      this.state = { data: { emailAddress: "Зарегистрироваться" }, email: 'Зарегистрироваться' };
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {    this.setState({email: event.target.value});  }
    handleSubmit(event) {
        const validationResult = validate(this.state.data, constraints);
         // validationResult is undefined if there are no errors
        this.setState({ errors: JSON.stringify(validationResult.emailAddress[0])});
        this.props.renderSwitch(1);
        event.preventDefault();
    }

    render() {
      return (
        <Container className="p-3" visible={this.props.visible}>
            <Row style={{height:200}}>
                <Col>
                    <img src="https://factoringplus.ru/images/logo.svg"></img>
                </Col>
                <Col style={{right:0}} ref={this.email}>
                    <label>{this.state.email}</label>
                </Col>
            </Row>
            <Row>
                <Col>
                    <Form>
                        <Form.Group controlId="formBasicEmail">
                            <Form.Label>Введите ваш email</Form.Label>
                            <Form.Control type="email" placeholder="Enter email"/>
                            <Form.Text className="text-muted" style={{color:"#ff0000"}}>{this.state.errors}</Form.Text>
                        </Form.Group>
                        <Form.Group controlId="formBasicPassword">
                            <Form.Label>Задайте пароль</Form.Label>
                            <Form.Control type="password" placeholder="Password" />
                        </Form.Group>
                        <Form.Group controlId="retypeBasicPassword">
                            <Form.Label>Повторите пароль</Form.Label>
                            <Form.Control type="password" placeholder="Password" />
                        </Form.Group>
                        <Form.Group>
                            <input type="checkbox" id="claims"/>
                            <label>Согласен со всеми claims</label>
                        </Form.Group>
                        <Button variant="primary" onClick={this.handleSubmit}>
                            Отправить
                        </Button>
                    </Form>
                </Col>
            </Row>
        </Container>
      );
    }
  }

export default Form0;
